import React from "react";
import { Button, Typography } from "@mui/material";
import { makeStyles } from "@mui/styles";

// Custom styles
const useStyles = makeStyles((theme) => ({
  root: {
    position: "relative",
    height: "100vh",
    backgroundImage: "url('https://your-image-url.jpg')", // Replace with your image URL
    backgroundSize: "cover",
    backgroundPosition: "center",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    color: "white",
    padding: "20px",
  },
  overlay: {
    position: "absolute",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "rgba(0, 0, 0, 0.4)", // Dark transparent overlay
  },
  content: {
    position: "relative",
    textAlign: "center",
    backgroundColor: "rgba(255, 255, 255, 0.85)", // Semi-transparent background for the login box
    padding: "40px",
    borderRadius: "10px",
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
    maxWidth: "400px",
    width: "100%",
  },
  title: {
    fontSize: "2.5rem",
    fontWeight: 600,
    marginBottom: "20px",
  },
  subtitle: {
    fontSize: "1.1rem",
    fontWeight: 400,
    marginBottom: "30px",
  },
  button: {
    padding: "15px",
    fontSize: "1.1rem",
    marginBottom: "15px",
    transition: "all 0.3s ease",
    "&:hover": {
      transform: "translateY(-3px)",
      boxShadow: "0 5px 15px rgba(0, 0, 0, 0.1)",
    },
  },
  buttonPrimary: {
    backgroundColor: "#007bff",
    color: "#fff",
    "&:hover": {
      backgroundColor: "#0056b3",
    },
  },
  buttonSecondary: {
    backgroundColor: "#28a745",
    color: "#fff",
    "&:hover": {
      backgroundColor: "#218838",
    },
  },
}));

const LoginPage = ({ onLogin }) => {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <div className={classes.overlay}></div>
      <div className={classes.content}>
        <Typography variant="h4" className={classes.title}>
          Welcome to Green Energy Platform
        </Typography>
        <Typography variant="h6" className={classes.subtitle}>
          Choose your role to log in and get started
        </Typography>
        <div>
          <Button
            variant="contained"
            fullWidth
            className={`${classes.button} ${classes.buttonPrimary}`}
            onClick={() => onLogin("provider")}
          >
            Login as Green Energy Provider
          </Button>
          <Button
            variant="contained"
            fullWidth
            className={`${classes.button} ${classes.buttonSecondary}`}
            onClick={() => onLogin("customer")}
          >
            Login as Customer
          </Button>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
