import React, { useState } from "react";
import { Typography, Button, Card, CardContent } from "@mui/material";

const energyProviders = [
  { id: 1, name: "Solar Energy Solutions", location: "123 Solar St.", availableSlots: 20 },
  { id: 2, name: "Green Wind Power", location: "456 Windy Ave.", availableSlots: 15 },
  { id: 3, name: "Eco Hydro Power", location: "789 Water Ln.", availableSlots: 25 },
];

const GreenEnergyProviderPage = () => {
  const [showProviders, setShowProviders] = useState(false);

  const handleViewProviders = () => {
    setShowProviders(true);
  };

  const handleScheduleAppointment = (providerId) => {
    alert(`Appointment scheduled for Green Energy Provider ID: ${providerId}`);
  };

  return (
    <div className="min-h-screen bg-gray-100 flex justify-center items-center py-8 px-4">
      <div className="w-full max-w-3xl bg-white p-8 rounded-xl shadow-xl">
        <Typography variant="h4" className="text-center text-green-600 mb-6">
          Green Energy Provider Dashboard
        </Typography>

        {!showProviders && (
          <div className="flex justify-center">
            <Button
              variant="contained"
              color="primary"
              className="px-8 py-3 rounded-lg text-lg font-semibold hover:bg-green-700 transition"
              onClick={handleViewProviders}
            >
              View Energy Providers
            </Button>
          </div>
        )}

        {showProviders && (
          <div>
            <Typography variant="h5" className="text-center text-gray-700 mb-6">
              Available Energy Providers
            </Typography>

            {energyProviders.map((provider) => (
              <Card key={provider.id} className="mb-6 p-6 rounded-lg shadow-md hover:shadow-xl transition">
                <CardContent>
                  <Typography variant="h6" className="text-xl font-semibold text-gray-800">
                    {provider.name}
                  </Typography>
                  <Typography color="textSecondary" className="text-gray-600 mt-2">
                    Location: {provider.location}
                  </Typography>
                  <Typography color="textSecondary" className="text-gray-600 mt-1">
                    Available Appointments: {provider.availableSlots}
                  </Typography>
                </CardContent>
                <div className="flex justify-center mt-4">
                  <Button
                    variant="contained"
                    color="secondary"
                    onClick={() => handleScheduleAppointment(provider.id)}
                    className="px-8 py-3 rounded-lg text-lg font-semibold bg-blue-600 hover:bg-blue-700 transition"
                  >
                    Schedule Appointment
                  </Button>
                </div>
              </Card>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default GreenEnergyProviderPage;
